<?phprequire_once('uploadedFile.php');$imagesCount = $_POST['imagesCount'];$images = array();for ($i = 1; $i <= $imagesCount; $i++) {    $images[] = $_POST['image' . $i];}$keywords = $_POST['keywords'];$savedImages = UploadedFile::moveTempFilesDescriptiveNames($images, $keywords);$imageSavingResult = 'Images saved as:'                   . '<ul><li>' . implode($savedImages, '</li><li>') . '</li></ul>';echo $imageSavingResult;/* * For files that contain only PHP code, the closing tag ("?>") is to be omitted. * It is not required by PHP, and omitting it prevents trailing whitespace from * being accidentally injected into the output. * ?> */